    ros::Time time;
    ros::Duration period(0.1);
    while (ros::ok()) { 
        if (isInitialized && cm) {
	    time = ros::Time::now();
            robot_hw->read(time, period);
            cm->update(time, period);
            robot_hw->write(time, period);
        }
        ros::spinOnce();
        period.sleep();
    }
    spinner.stop();
  return 0;
}

